// src/pages/HomePage.js

import { Link } from 'react-router-dom';



import React from 'react';
import SearchBar from '../components/SearchBar';
import BookList from '../components/BookList';
import Categories from '../components/Categories';
import Footer from '../components/Footer';
import './HomePage.css';

const HomePage = () => {
  const popularBooks = [
    { id: 1, title: 'Book 1', author: 'Author 1' },
    { id: 2, title: 'Book 2', author: 'Author 2' },
    // Add more popular books here
  ];

  const recentBooks = [
    { id: 1, title: 'Recent Book 1', author: 'Author 1' },
    { id: 2, title: 'Recent Book 2', author: 'Author 2' },
    // Add more recent books here
  ];

  const categories = [
    { id: 1, name: 'Science' },
    { id: 2, name: 'Arts' },
    { id: 3, name: 'Technology' },
    // Add more categories here
  ];

  return (
    <div className="homepage">
      <header className="welcome-message">
      <Link to="/">h</Link>
        <h1>Welcome to the E-Library</h1>
        <p>Discover your next great read.</p>
      </header>
      <SearchBar />
      <section className="book-section">
        <h2>Popular Books</h2>
        <BookList books={popularBooks} />
      </section>
      <section className="book-section">
        <h2>Recently Added Books</h2>
        <BookList books={recentBooks} />
      </section>
      <section className="category-section">
        <h2>Categories</h2>
        <Categories categories={categories} />
      </section>
      <Footer />
    </div>
  );
};

export default HomePage;
